  youtubeEmbedUrl = `https://www.youtube.com/embed/${videoId}`;
}

// ëª¨ë“  ì…ë ¥ ë°ì´í„° ê°€ì ¸ì˜¤ê¸°
const allInputs = $input.all();

// ì±•í„° ë‚´ìš© ì¶”ì¶œ í•¨ìˆ˜
function getChapterContent(inputItem) {
  if (!inputItem) return '<p>ë‚´ìš©ì„ ë¶ˆëŸ¬ì˜¬ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.</p>';

  let content = '';

  // ë‹¤ì–‘í•œ ë°©ì‹ìœ¼ë¡œ ë‚´ìš© ì¶”ì¶œ ì‹œë„
  if (inputItem.json) {
    if (typeof inputItem.json.data === 'string') {
      content = inputItem.json.data;
    } else if (typeof inputItem.json === 'string') {
      content = inputItem.json;
    } else if (inputItem.json.content) {
      content = inputItem.json.content;
    } else if (inputItem.json.body) {
      content = inputItem.json.body;
    }
  }

  // binary ë°ì´í„° ì²˜ë¦¬
  if (!content && inputItem.binary && inputItem.binary.data) {
    try {
      if (typeof inputItem.binary.data === 'string') {
        content = inputItem.binary.data;
      } else if (inputItem.binary.data.toString) {
        content = inputItem.binary.data.toString();
      }
    } catch (e) {
      console.log('Binary ë³€í™˜ ì‹¤íŒ¨:', e);
    }
  }

  if (!content) {
    return '<p>ë‚´ìš©ì„ ë¶ˆëŸ¬ì˜¬ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.</p>';
  }

  // HTML body ë‚´ìš©ë§Œ ì¶”ì¶œ
  const bodyMatch = content.match(/<body[^>]*>([\s\S]*?)<\/body>/i);
  if (bodyMatch && bodyMatch[1]) {
    return bodyMatch[1].trim();
  }

  // body íƒœê·¸ê°€ ì—†ìœ¼ë©´ ë¶ˆí•„ìš”í•œ íƒœê·¸ë“¤ ì œê±°
  return content
    .replace(/<html[^>]*>/gi, '')
    .replace(/<\/html>/gi, '')
    .replace(/<head[^>]*>[\s\S]*?<\/head>/gi, '')
    .replace(/<\/?body[^>]*>/gi, '')
    .replace(/<\/?doctype[^>]*>/gi, '')
    .trim();
}

