// =================================================================
// 🔧 올바른 n8n Code 노드 (HTML을 JavaScript로 생성)
// =================================================================

// -----------------------------------------------------------------
// ⚙️ 1단계: 데이터 가져오기
// -----------------------------------------------------------------

// YouTube 정보 가져오기
let videoInfo = {};
try {
  const youtubeNode = $('Search records1').first();
  if (youtubeNode && youtubeNode.json) {
    videoInfo = {
      Name: youtubeNode.json.Name || "제$('Search records1').first().json.page_number장",
      Youtube_URL: youtubeNode.json.Youtube_URL || "",
      page_number: youtubeNode.json.page_number || "audiobook"
    };
  }
} catch (error) {
  videoInfo = {
    Name: "통합 오디오북",
    Youtube_URL: "",
    page_number: "audiobook"
  };
}

// YouTube URL 처리
let youtubeEmbedUrl = '';
if (videoInfo.Youtube_URL) {
  let videoId = videoInfo.Youtube_URL;

  if (videoId.includes('youtube.com/watch?v=')) {
    videoId = videoId.split('v=')[1].split('&')[0];
  } else if (videoId.includes('youtu.be/')) {
    videoId = videoId.split('youtu.be/')[1].split('?')[0];
  } else if (videoId.includes('youtube.com/embed/')) {
    videoId = videoId.split('embed/')[1].split('?')[0];
  }

  youtubeEmbedUrl = `https://www.youtube.com/embed/${videoId}`;
}

// 모든 입력 데이터 가져오기
const allInputs = $input.all();

// 챕터 내용 추출 함수
function getChapterContent(inputItem) {
  if (!inputItem) return '<p>내용을 불러올 수 없습니다.</p>';

  let content = '';

  // 다양한 방식으로 내용 추출 시도
  if (inputItem.json) {
    if (typeof inputItem.json.data === 'string') {
      content = inputItem.json.data;
    } else if (typeof inputItem.json === 'string') {
      content = inputItem.json;
    } else if (inputItem.json.content) {
      content = inputItem.json.content;
    } else if (inputItem.json.body) {
      content = inputItem.json.body;
    }
  }

  // binary 데이터 처리
  if (!content && inputItem.binary && inputItem.binary.data) {
    try {
      if (typeof inputItem.binary.data === 'string') {
        content = inputItem.binary.data;
      } else if (inputItem.binary.data.toString) {
        content = inputItem.binary.data.toString();
      }
    } catch (e) {
      console.log('Binary 변환 실패:', e);
    }
  }

  if (!content) {
    return '<p>내용을 불러올 수 없습니다.</p>';
  }

  // HTML body 내용만 추출
  const bodyMatch = content.match(/<body[^>]*>([\s\S]*?)<\/body>/i);
  if (bodyMatch && bodyMatch[1]) {
    return bodyMatch[1].trim();
  }

  // body 태그가 없으면 불필요한 태그들 제거
  return content
    .replace(/<html[^>]*>/gi, '')
    .replace(/<\/html>/gi, '')
    .replace(/<head[^>]*>[\s\S]*?<\/head>/gi, '')
    .replace(/<\/?body[^>]*>/gi, '')
    .replace(/<\/?doctype[^>]*>/gi, '')
    .trim();
}

// -----------------------------------------------------------------
// ⚙️ 2단계: 챕터별 내용 생성
// -----------------------------------------------------------------

const chapters = [];
for (let i = 0; i < 8; i++) {
  chapters.push({
    number: i + 1,
    title: `챕터 ${i + 1}`,
    content: getChapterContent(allInputs[i])
  });
}

// -----------------------------------------------------------------
// ⚙️ 3단계: HTML 생성
// -----------------------------------------------------------------

const finalHTML = `<!DOCTYPE html>
<html lang="ko">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>${videoInfo.Name}</title>
    <style>
        :root { 
            --sidebar-width: 300px; 
        }

        body { 
            font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif; 
            margin: 0; 
            background-color: #f4f7f9; 
        }

        /* 제목 섹션 */
        .title-section {
            background: #ffffff;
            padding: 30px 40px;
            text-align: center;
            border-bottom: 1px solid #e0e0e0;
        }

        .main-title {
            font-size: 2.5em;
            font-weight: bold;
            color: #2c3e50;
            margin: 0;
        }

        /* 영상 섹션 */
        .video-section {
            background: #ffffff;
            padding: 40px;
            display: flex;
            justify-content: center;
            border-bottom: 1px solid #e0e0e0;
        }

        .video-container {
            width: 100%;
            max-width: 800px;
        }

        .youtube-iframe { 
            width: 100%; 
            aspect-ratio: 16 / 9; 
            border: none; 
            border-radius: 12px; 
            box-shadow: 0 4px 15px rgba(0,0,0,0.1); 
            background-color: #e9ecef; 
        }

        /* 콘텐츠 영역 */
        .content-section {
            display: flex;
            min-height: calc(100vh - 400px);
        }

        /* 왼쪽 네비게이션 */
        .sidebar { 
            width: var(--sidebar-width); 
            background: #ffffff; 
            padding: 30px 20px;
            box-shadow: 2px 0 8px rgba(0,0,0,0.05); 
            box-sizing: border-box;
            overflow-y: auto;
        }

        .sidebar h3 { 
            margin-top: 0; 
            margin-bottom: 25px; 
            font-size: 1.3em; 
            color: #34495e;
            text-align: center;
        }

        .nav-btn { 
            display: block; 
            width: 100%; 
            padding: 16px 20px; 
            margin-bottom: 10px; 
            background: transparent; 
            border: 1px solid #dcdcdc; 
            cursor: pointer; 
            border-radius: 8px; 
            text-align: left; 
            transition: all 0.2s ease; 
            font-size: 16px; 
            color: #34495e; 
        }

        .nav-btn:hover { 
            background: #f0f4f7; 
            border-color: #007bff; 
        }

        .nav-btn.active { 
            background: #007bff; 
            color: white; 
            border-color: #007bff; 
            font-weight: 600; 
        }

        /* 오른쪽 콘텐츠 영역 */
        .main-content { 
            flex: 1; 
            padding: 40px;
            background: #ffffff;
            overflow-y: auto;
        }

        .content-page { 
            display: none; 
            animation: fadeIn 0.4s ease; 
        }

        .content-page.active { 
            display: block; 
        }

        .chapter-title { 
            font-size: 2.0em; 
            margin-bottom: 25px; 
            color: #2c3e50; 
            border-bottom: 3px solid #007bff; 
            padding-bottom: 15px; 
        }

        .chapter-content { 
            line-height: 1.8; 
            font-size: 0.9em; 
            color: #333; 
        }

        /* 컨텐츠 내 요소들 스타일링 */
        .chapter-content img {
            max-width: 100%;
            height: auto;
            border-radius: 8px;
            margin: 15px 0;
        }

        .chapter-content table {
            width: 100%;
            border-collapse: collapse;
            margin: 20px 0;
        }

        .chapter-content th,
        .chapter-content td {
            padding: 12px;
            border: 1px solid #ddd;
            text-align: left;
        }

        .chapter-content th {
            background-color: #f8f9fa;
            font-weight: bold;
        }

        .chapter-content p {
            margin-bottom: 16px;
        }

        .chapter-content h1, .chapter-content h2, .chapter-content h3 {
            color: #2c3e50;
            margin-top: 30px;
            margin-bottom: 15px;
        }

        @keyframes fadeIn { 
            from { 
                opacity: 0; 
                transform: translateY(10px); 
            } 
            to { 
                opacity: 1; 
                transform: translateY(0); 
            } 
        }

        @media (max-width: 768px) {
            .content-section {
                flex-direction: column;
            }
            .sidebar {
                width: 100%;
                max-height: 200px;
                padding: 20px;
            }
            .main-content {
                padding: 20px;
            }
            .title-section, .video-section {
                padding: 20px;
            }
            .main-title {
                font-size: 2em;
            }
        }
    </style>
</head>
<body>
    <!-- 제목 섹션 -->
    <div class="title-section">
        <h1 class="main-title">${videoInfo.Name}</h1>
    </div>

    <!-- 영상 섹션 -->
    <div class="video-section">
        <div class="video-container">
            ${youtubeEmbedUrl ? `
            <iframe class="youtube-iframe" 
                    src="${youtubeEmbedUrl}" 
                    title="YouTube video player" 
                    frameborder="0" 
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" 
                    allowfullscreen>
            </iframe>
            ` : '<div class="youtube-iframe" style="display: flex; align-items: center; justify-content: center; color: #666;">YouTube 영상이 없습니다.</div>'}
        </div>
    </div>

    <!-- 콘텐츠 섹션 (네비게이션 + 내용) -->
    <div class="content-section">
        <!-- 왼쪽 네비게이션 -->
        <div class="sidebar">
            <h3>📚 목차</h3>
            ${chapters.map((chapter, index) => 
              `<button class="nav-btn ${index === 0 ? 'active' : ''}" onclick="showPage(${chapter.number})">${chapter.number}. ${chapter.title}</button>`
            ).join('\n            ')}
        </div>

        <!-- 오른쪽 콘텐츠 영역 -->
        <div class="main-content">
            ${chapters.map((chapter, index) => `
            <!-- 챕터 ${chapter.number} -->
            <div id="page-${chapter.number}" class="content-page ${index === 0 ? 'active' : ''}">
                <h2 class="chapter-title">${chapter.title}</h2>
                <div class="chapter-content">
                    ${chapter.content}
                </div>
            </div>`).join('\n            ')}
        </div>
    </div>

    <script>
        function showPage(pageNumber) {
            // 모든 네비게이션 버튼에서 active 클래스 제거
            document.querySelectorAll('.nav-btn').forEach(btn => {
                btn.classList.remove('active');
            });

            // 모든 콘텐츠 페이지에서 active 클래스 제거
            document.querySelectorAll('.content-page').forEach(page => {
                page.classList.remove('active');
            });

            // 클릭된 네비게이션 버튼에 active 클래스 추가
            const activeNav = document.querySelector(\`button[onclick="showPage(\${pageNumber})"]\`);
            if (activeNav) {
                activeNav.classList.add('active');
            }

            // 해당 페이지에 active 클래스 추가
            const activePage = document.getElementById('page-' + pageNumber);
            if (activePage) {
                activePage.classList.add('active');
            }
        }

        // 페이지 로드 시 첫 번째 페이지 활성화
        document.addEventListener('DOMContentLoaded', function() {
            showPage(1);
        });
    </script>
</body>
</html>`;

// -----------------------------------------------------------------
// ⚙️ 4단계: 결과 반환
// -----------------------------------------------------------------

return [{
    json: {
        fileName: `${videoInfo.page_number}.html`,
        finalHTML: finalHTML,
        debug: {
            videoName: videoInfo.Name,
            youtubeUrl: youtubeEmbedUrl,
            chaptersCount: chapters.length,
            inputItemsCount: allInputs.length,
            chapterContentLengths: chapters.map((ch, i) => ({
                chapter: i + 1,
                contentLength: ch.content.length,
                hasContent: ch.content.length > 50
            }))
        }
    }
}];
